import { Component, EventEmitter, Input, Output } from "@angular/core";

@Component({
  template: `
  <h1>{{title}}</h1>
  <h2>you succeed!!!</h2>(
  <br>
  <p> {{getWish()}}</p>`,

  selector: 'app-root',
  templateUrl: 'app.component.html'

})
export class AppComponent {
 
  title: string = "hello! this is the beautifull title in the world.........!";


  getWish() {

    if (Date.now().toString() > "2023/11/1")
      return "good morning!";
    else
      return "good evening!";
  }

}
