import { Component, OnInit } from '@angular/core';
import { Course, LearningWay } from '../course.model';
import { CourseService } from '../course.service';
import { Router } from '@angular/router';
import { Category } from '../../categories/category.model';
import { CategoryService } from '../../categories/category.service';

@Component({
  selector: 'app-all-courses',
  templateUrl: './all-courses.component.html',
  styleUrls: ['./all-courses.component.scss']
})
export class AllCoursesComponent implements OnInit {
  selectedCourse?:Course;
  courses: Course[] = [];
  ALLCOURSES: Course[] = [];
  categories: Category[] = [];
  learning=Object.values(LearningWay);
  constructor(private _courseService :CourseService,private _categoryService:CategoryService,private router: Router) {

  }
  ngOnInit(): void {
    this._courseService.getCoursesFromServer().subscribe(data=>{
      this.ALLCOURSES=data;
      this.courses=data;
    })
    this._categoryService.getCategoriesFromServer().subscribe(data=>{
      this.categories=data;
      console.log("categories",this.categories)
    })
  }
  selectCourse(c:Course){
    this.selectedCourse=c;
    // this.router.navigate(['/courseDetails'], { queryParams: { course: c } });
  }
  changeCourseName(name:any){
    console.log("name",name)
    this.courses=this.ALLCOURSES.filter(x=>x.name==name.target.value||name.target.value=="")
  }
  select(n:Category){}
}

<div class="container">
    <select name="category" id="category">
        <option *ngFor="let n of categories"(selected)="select(n)">{{n.name}}</option>
    </select>
    <input type="text" name="courseName" placeholder="Enter Course Name" (change)="changeCourseName($event)">
    <select name="learningWay" id="learningWay">
        <option *ngFor="let c of learning" >{{c}}</option>
    </select>
</div>
<div class="container">

    <ul class="coursesFlex list-group">
        <li class="list-group-item" style="width:250px" *ngFor="let c of courses">
            <h4>{{c.name}}</h4>
            <div>{{c.lessonsAmount}} lessons</div>
            <!-- <img *ngIf="c.learningWay==Frontal" src="../../../../assets/phone-1.png" alt="">
        <img *ngIf="c.learningWay==Zoom" src="../../../../assets/tiktok.png" alt=""> -->

            <button (click)="selectCourse(c)" class="btn btn-primary">Details</button>
        </li>
    </ul>
</div>

<app-course-details class="container" *ngIf="selectedCourse" [course]="selectedCourse"></app-course-details>
