import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Student, YearS } from '../student.model';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { AppStudings, Studying } from '../studing.model';
import {Sum}
@Component({
  selector: 'single-student-details',
  templateUrl: './single-student-details.component.html',
  styleUrls: ['./single-student-details.component.scss']
})
export class SingleStudentDetailsComponent {
  year = YearS;
  private _student?: Student;
  studingList: Studying[] = AppStudings;

  studentForm: FormGroup = new FormGroup({});
  sumDays:number|undefined;
  private _numDays: number | undefined;
  public get numDays(): number | undefined {
    return this._numDays;
  }
  public set numDays(v: number | undefined) {
    this._numDays = v;
  }
  private _dateStart: Date | undefined;
  public get dateStart(): Date | undefined {
    return this._dateStart;
  }
  public set dateStart(v: Date | undefined) {
    this._dateStart = v;
  }
  public get student(): Student | undefined {
    return this._student;
  }
  @Input()
  public set student(value: Student | undefined) {
    this._student = value;
    if (this._student != undefined) {
      this.studentForm = new FormGroup({
        "id": new FormControl(this._student.id, Validators.required),
        "name": new FormControl(this._student.name, [Validators.required, Validators.minLength(2)]),
        "family": new FormControl(this._student.family, [Validators.required, Validators.minLength(2)]),
        "addres": new FormControl(this._student.addres),
        "phon": new FormControl(this._student.phon, [Validators.minLength(9), Validators.maxLength(10)]),
        "works": new FormControl(this._student.works),
        "avg": new FormControl(this._student.avg),
        "dateLeave": new FormControl(this._student.dateLeave),
        "studyingId": new FormControl(this._student.studyingId),
        "year": new FormControl(this._student.year),
        "test": new FormControl(this._student.tests)
      });
    }
  }
  ngOnInit(): void {

  }

  @Output()
  onSaveNewStudent: EventEmitter<Student> = new EventEmitter();
  @Output()
  onDeleateStudent: EventEmitter<Student> = new EventEmitter();

  saveNewStudent() {
    this.onDeleateStudent.emit(this._student);
    this._student = this.studentForm.value;

    alert("ההוספה בוצעה בהצלחה");
    alert(this._student?.addres + "" + this._student?.avg + "" + this._student?.family + "" + this._student?.id + "" + this._student?.name + "" + this._student?.phon);
    this.onSaveNewStudent.emit(this._student);
  }

  constructor(private _sumDaysH:SumDaysH) { 

  }

}
