})
export class StudentsListComponent implements OnInit{
  students:Student[]=[{id:1,name:"fghj",phon:"13456",family:"jk",addres:"hjk 5",avg:89,works:false,dateL},{id:2,name:"fgk",phon:"1345678956",family:"hjkjk",addres:"hjk 9",avg:100,works:true}];
  ngOnInit(): void {
  }
  selectedStudent!: Student;
constructor(){}
deleteStudent(student:Student){
  let indexOfStudent=this.students.indexOf(student);
