import { Injectable } from "@angular/core";
import { Student } from "./student.model";
import { Test } from "./test.model";
let studentslist =
   [new Student("fghj",1,  "jk",  "hjk 5",  "13456","d@s" , 89,undefined,true,  [new Test(1, new Date(), "jkk" ,90),new Test(2, new Date(), "yy",90 )],undefined ),new Student( "fgk",2,"hjkjk","gg", "1345678956","h@else", 67,undefined,true,undefined,undefined )];

@Injectable()
export class studentService {
   students:Student[]|undefined=studentslist;
   getStudents(): Student[] |undefined{
      return this.students;
   }
   setStudents(student:Student|undefined) {
      let index=this.students?this.students.findIndex(s=>student?.id==s.id):-1;
      if(index>0){
         this.students?.splice(index);
      }
      student&&this.students?this.students.push(student):0;
   }
   deleteStudent(student:Student|undefined){
      let index=this.students?this.students.findIndex(s=>student?.id==s.id):-1;
      if(index>-1){
         this.students?.splice(index);
      }
   }
   
  timer: Observable<Date> = new Observable(obs => {
   setInterval(() => {
     obs.next(new Date());
   }, 1000);
