import { Injectable } from "@angular/core";
import { Student } from "./student.model";
let students =
   [{ , name: "fghj", phon: "13456", family: "jk", addres: "hjk 5", avg: 89,active:true,dateLeave:undefined, tests: [{ code: 1, date: new Date(), description: "jkk" ,mark:90}, { code: 2, date: new Date(), description: "yy",mark:90 }] },new Student(id: 2, name: "fgk", phon: "1345678956", family: "hjkjk",active:true, addres: "hjk 9", avg: 100)];

@Injectable()
export class studentService {
   getStudents(): Student[] {
      return students;
   }
   setStudents(student:Student) {
      let index=students.findIndex(s=>student.id==s.id);
      if(index>0){
         students.filter(s=>s.id!=student.id);
      }
      students.push(student);
   }
   getStudentsAfterSeconds(): Promise<Student[]> {
      return new Promise((res, rej) => {
         setTimeout(() => {
            res(students);
         }, 3000)
      });
   }
}
