import { Component, Input } from '@angular/core';
import { Test } from '../test.model';
import { SAvgService } from 'src/sAvgService.service';
@Component({
  selector: 'test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.scss']
})
export class TestComponent {

  private _testList?:Test[]|undefined;
  private _idStudent?:number;
  public avg?:number;
  public get tests(): Test[]|undefined{
    return this._testList;
  } 
   @Input()
  public set tests(value: Test[] | undefined) {
    this._testList=value;
  }

  public get idStudent(): number | undefined{
    return this._idStudent;
  } 
   @Input()
  public set idStudent(value: number| undefined) {
    this._idStudent=value;
  }
  ngOnInit(): void {
  }
  constructor(private _sAvgService:SAvgService ){
  this.avg=_sAvgService.getAvgById(this._idStudent);
  }
}

<ul  class="list-group"*ngIf="tests">
    <h3>The tests</h3>
    <p>The average is:{{avg}}</p>
    <li *ngFor="let test of tests">
        <div class="row">
            <div class="col-2">{{test.code}}</div>
            <div class="col-2">{{test.date}}</div>
            <div class="col-2">{{test.description}}</div>
            <div class="col-2">{{test.mark}}</div>
        </div>
    </li>
</ul>
