import { Component, Input } from '@angular/core';
import { Test } from '../test.model';

@Component({
  selector: 'test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.scss']
})
export class TestComponent {

  private _testList?:Test[];

  public get student(): Test[] | undefined{
    return this._testList;
  } 
   @Input()
  public set tests(value: Test[] | undefined) {
    this._testList=value;
  }

  ngOnInit(): void {
  }
  constructor(){

  }
}
<ul  class="list-group">
    <li *ngFor="let test of tests">
        <div class="row">
            <div class="col-2">{{test.code}}</div>
            <div class="col-2">{{test.date}}</div>
            <div class="col-2">{{test.description}}</div>
            <div class="col-2">{{test.mark}}</div>
        </div>
    </li>
</ul>
