import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Student, YearS } from '../student.model';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { AppStudings, Studying } from '../studing.model';
import { SumDaysH } from '../sumDaysH.service';
import { studentService } from '../studentService.service';
@Component({
  selector: 'single-student-details',
  templateUrl: './single-student-details.component.html',
  styleUrls: ['./single-student-details.component.scss']
})
export class SingleStudentDetailsComponent {
  sS?:studentService;
  year = YearS;
  private _student?: Student;
  studingList: Studying[] = AppStudings;
  dateLeave:Date | undefined;
  sumDays:number;
  studentForm: FormGroup = new FormGroup({});
  startMissingDate: Date | undefined;
  numMissingDays: number | undefined;
 totalMissingDate: number|undefined;
  public get student(): Student | undefined {
    return this._student;
  }
  @Input()
  public set student(value: Student | undefined) {
    this._student = value;
    if (this._student != undefined) {
      this.studentForm = new FormGroup({
        "id": new FormControl(this._student.id, Validators.required),
        "name": new FormControl(this._student.name, [Validators.required, Validators.minLength(2)]),
        "family": new FormControl(this._student.family, [Validators.required, Validators.minLength(2)]),
        "addres": new FormControl(this._student.addres),
        "phon": new FormControl(this._student.phon, [Validators.minLength(9), Validators.maxLength(10)]),
        "mail": new FormControl(this._student.mail, [Validators.email]),
        "avg": new FormControl(this._student.avg),
        "dateLeave": new FormControl(this._student.dateLeave),
        "year": new FormControl(this._student.year),
        "test": new FormControl(this._student.tests)
      });
      this.startMissingDate=this.studentForm.controls['dateStart'].value;
      this.numMissingDays=this.studentForm.controls['numMissingDays'].value;
    }
  }
  ngOnInit(): void {

  }

  @Output()
  onSaveNewStudent: EventEmitter<Student> = new EventEmitter();
  @Output()
  onDeleateStudent: EventEmitter<Student> = new EventEmitter();

  saveNewStudent() {
    this.onDeleateStudent.emit(this._student);
    this._student = this.studentForm.value;
    if(this.numMissingDays&&this.numMissingDays>0)
    this._student?.absenceDays?.push({dateStart:this.startMissingDate,numDays:this.numMissingDays});
    if(this.dateLeave!=undefined){
    this._student?.active? this._student.active=false:true;
    }

    this.sS?.setStudents(this._student);
    alert("ההוספה בוצעה בהצלחה");
    alert(this._student?.addres + "" + this._student?.avg + "" + this._student?.family + "" + this._student?.id + "" + this._student?.name + "" + this._student?.phon);
    this.onSaveNewStudent.emit(this._student);
  }

  constructor(private _sumDaysH: SumDaysH,private _studentService:studentService ){
    this.sS=_studentService;
  this.sumDays=_sumDaysH.getSum(this._student?.id);
  }

}
<div class="card" *ngIf="student">
    <div class="card-body">
        <form [formGroup]="studentForm" [ngClass]="{'okay':studentForm.valid}">
            <div class="form-group">
                <label for="id">Student id</label>
                <input type="number" class="form-control" id="id" formControlName="id">
                <ng-container *ngIf="studentForm.controls['id'].errors">
                    <div class="alert alert-danger" *ngIf="studentForm.controls['id'].errors['required']">This field is required</div>
                </ng-container>
            </div>
            <div class="form-group">
                <label for="name">Student name</label>
                <input type="text" class="form-control" id="name" formControlName="name">
                <ng-container *ngIf="studentForm.controls['name'].errors">
                    <div class="alert alert-danger" *ngIf="studentForm.controls['name'].errors['required']">This field is required</div>
                    <div class="alert alert-danger" *ngIf="studentForm.controls['name'].errors['minlength']">This field is must be at least
                        2 chars</div>
                </ng-container>
            </div>

            <div class="form-group">
                <label for="family">Student family</label>
                <input type="text" class="form-control" id="family" formControlName="family">
                <ng-container *ngIf="studentForm.controls['family'].errors">
                    <div class="alert alert-danger" *ngIf="studentForm.controls['family'].errors['required']">This field is required</div>
                    <div class="alert alert-danger" *ngIf="studentForm.controls['family'].errors['minlength']">This field is must be at least
                        2 chars</div>
                </ng-container>
            </div>

            <div class="form-group">
                <label for="addres">Student addres</label>
                <input type="text" class="form-control" id="addres" formControlName="addres">

            </div>

            <div class="form-group">
                <label for="phon">Student phon</label>
                <input type="tel" class="form-control" id="phon" formControlName="phon">
                <ng-container *ngIf="studentForm.controls['phon'].errors">
                    <div class="alert alert-danger" *ngIf="studentForm.controls['phon'].errors['minlength']">This field is must be at least
                        9 chars</div>
                        <div class="alert alert-danger" *ngIf="studentForm.controls['phon'].errors['maxlength']">This field is must be not mare than
                           10 chars</div>
                </ng-container>
            </div>
            <div class="form-group">
                <label for="mail">Student email</label>
                <input type="email" class="form-control" id="mail" formControlName="mail">
                <ng-container *ngIf="studentForm.controls['mail'].errors">
                    <div class="alert alert-danger" *ngIf="studentForm.controls['mail'].errors['required']">This field is required</div>
                    <div class="alert alert-danger" *ngIf="studentForm.controls['mail'].errors['email']">email must </div>
                </ng-container>
            </div>
            <div class="form-group">
                <label for="avg">Student avg</label>
                <input type="number" class="form-control" id="avg" formControlName="avg">
            </div>
            <div class="form-group" *ngIf="student.active">
                <label for="dateLeave">Student dateLeave</label>
                <input type="date" class="form-control" id="dateLeave" formControlName="dateLeave">
            </div>
            <div class="form-group" *ngIf="!student.active">
                <label for="dateLeave"class="dateLeave">The student leaved</label>
            </div>
            <div class="form-group">
                <label for="works">Student works</label>
                <input type="text" class="form-control" id="works" formControlName="works">
            </div>

            <div class="form-group">
                <label for="studyingId">User:</label>
                <select class="form-control" formControlName="studyingId">
                    <option *ngFor="let studing of studingList" [value]="studing.id">{{studing.name}}</option>
                </select>
            </div>

            <div class="form-group">
                <label for="year">Task Priority:</label>
                <br>
                <input type="radio" value="year.First" name="year" formControlName="year" />א
                <br>
                <input type="radio" value="year.Second" name="year" formControlName="year" />ב
                <br>
                <input type="radio" value="year.Third" name="year" formControlName="year" />ג
            </div>

            <div [ngSwitch]="studentForm.controls['year'].value">
