import { DatePipe } from "@angular/common";
import { Component, EventEmitter, Input, Output } from "@angular/core";

@Component({
  selector: 'app-root',
  templateUrl: 'app.component.html'

})
export class AppComponent {
 
  getWish() {

    if (Date.now().toString() > "2023/11/1")
      return "good morning!";
    else
      return "good evening!";
  }

}
