import { Injectable } from "@angular/core";
import { Student } from "./student.model";
import { studentService } from "./studentService.service";

@Injectable()
export class SumDaysH {
    students: Student[] = [];
    constructor(private _studentService: studentService) {
        this.students = _studentService.getStudents();
    }
    getStudents(id?: number): number {
        let sum: number = 0;
        const s = this.students.find(stu => stu.id == id);
        if (s == undefined) {
            return -1;
        }
        s.lDays?.forEach(element => {
            sum += element.numDays;
        });
        return sum;
    }
}
