import { Component, OnInit } from '@angular/core';
import { from, interval, Observable } from 'rxjs';
import { filter, map } from "rxjs/operators";
import { studentService } from '../studentService.service';
import { Student } from '../student.model';

@Component({
  selector: 'observable',
  templateUrl: './ObservableComponent.html'
})
export class ObservableComponent implements OnInit {
  students?: Student[] = [];
  constructor(private _studentService: studentService) {
    this.students = _studentService.getStudents();
    this.studentsNames.subscribe(
      (val) => {
        console.log("the name is: " + val);
      })
    this.studentsNames2.subscribe(
      (val) => {
        console.log("the name is: " + val);
      })
      this.timerByOperator.subscribe((val)=>{this.timerValue=val;})
    // this.timerByOperator.subscribe((val) => {
    //   this.timerValue = val.toLocaleTimeString();
    // })
  }

  timerValue: string = "";

  timer: Observable<string> = new Observable(obs => {
    setInterval(() => {
      obs.next(new Date().toLocaleTimeString(););
    }, 1000);
  });
  timerByOperator: Observable<string> = interval(1000).pipe(map(x => { return new Date().toLocaleTimeString();}));

  studentsNames: Observable<string> = new Observable(obs => {
    for (var i = 0; i < (this.students ? this.students.length : 0); i++) {
      obs.next(this.students ? this.students[i].name : "");
    }
  });

  studentsNames2: Observable<string> = from(this.students ? this.students : []).pipe(map(x => { return x.name; }))
  ngOnInit(): void {
  }

}
