import { from, interval, Observable } from 'rxjs';
import { filter, map } from "rxjs/operators";
import { studentService } from '../studentService.service';
import { Student } from '../student.model';

@Component({
  selector: 'observable',
  templateUrl: './ObservableComponent.html'
})
export class ObservableComponent implements OnInit {
  students?: Student[] = [];
  constructor(private _studentService: studentService) {
    this.students = _studentService.getStudents();
    this.studentsNames.subscribe(
      (val) => {
        console.log("the name is: " + val);
      })
    this.studentsNames2.subscribe(
      (val) => {
        console.log("the name is: " + val);
      })
      this.timer.subscribe((val)=>{this.timerValue=val;})
      this.mail.subscribe((val)=>{this.timerValue=val;})
    // this.timerByOperator.subscribe((val) => {
    //   this.timerValue = val.toLocaleTimeString();
    // })
  }

  timerValue: string = "";
