    setInterval(() => {
      obs.next(new Date().toLocaleTimeString());
    }, 1000);
  });
  timerByOperator: Observable<string> = interval(1000).pipe(map(x => { return new Date().toLocaleTimeString();}));
  mail:Observable<string>=new Observable((obs=>{
    this.students?.forEach(a => {
      if(a.active){
        obs.next("The email was send successfully to: "+a.mail);
      }
    });
  }));
