    for (var i = 0; i < (this.students ? this.students.length : 0); i++) {
      obs.next(this.students ? this.students[i].name : "");
    }
  });

  studentsNames2: Observable<string> = from(this.students ? this.students : []).pipe(map(x => { return x.name; }))
  ngOnInit(): void {
  }

}
