import { Component, OnInit } from '@angular/core';
import { UserService } from '../users/user.service';
import { User } from '../users/user.model';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit{
  users?:User[];
constructor(private _userService:UserService){

}
ngOnInit(): void {
  this._userService.getUsers().subscribe(u=>{this.users=u;},err=>{console.log("err")});
}
}
<form [formGroup]="usersForm">
    <div class="form-group">
        <label for="usr">Name:</label>
        <input type="text" class="form-control" id="usr" formControlName="name">
    </div>

    <div class="form-group">
        <label for="pwd">Password:</label>
        <input type="password" class="form-control" id="pwd" formControlName="password">
    </div>
</form>
<button class="btn btn-success" (click)="checkUser()">Login</button>
<ul class="list-group">
    <li class="list-group-item" *ngFor="let u of users">
        <div class="row">
            <div class="col-2">{{u.id}}</div>
            <div class="col-2">{{u.name}}</div>
        </div>
    </li>
</ul>
