import { Injectable } from "@angular/core";
import { Student } from "./modules/students/student.model";
import { studentService } from "./studentService.service";

@Injectable()
export class SumDaysH {
    students: Student[]|undefined = [];
    constructor(private _studentService: studentService) {
       _studentService.getStudents().subscribe(s=> this.students = s,err=>console.log("err"));
    }
    getSum(id?: number): number {
        let sum: number = 0;
        if (id != undefined) {
            const s = this.students?.find(stu => stu.id == id);
            if (s == undefined) {
                return -1;
            }
            s.absenceDays?.forEach(element => {
                sum +=element.numDays!=undefined?element.numDays:0;
            });
        }
        return sum;
    }
}
