import { Injectable } from "@angular/core";
import { studentService } from "./app/studentService.service";
import { Student } from "./app/student.model";

@Injectable()
export class SAvgService{
students:Student[]=[];
?:Student;
constructor(private _studentService:studentService ){
this.students=_studentService.getStudents();
}
getAvgById(id:Number):Number{
=this.students.find(stu=>stu.id==id);

}
}
