import { Student } from '../student.model';

@Component({
  selector: 'app-single-student-details',
  templateUrl: './single-student-details.component.html',
  styleUrls: ['./single-student-details.component.scss']
})
export class SingleStudentDetailsComponent {
@Input()
student!:Student;

  ngOnInit(): void {

  }
   @Output()
  onSaveNewTask: EventEmitter<Task> = new EventEmitter();

  saveNewTask() {
    this.onSaveNewTask.emit(this.task);
    //this.tasks.push(this.selectedTask);
  }

constructor(){}

}
