import { Component, Input, OnInit } from '@angular/core';
import { Course, LearningWay } from '../course.model';
import { User } from '../../users/user.model';
import { Category } from '../../categories/category.model';
import { CategoryService } from '../../categories/category.service';
@Component({
  selector: 'app-course-details',
  templateUrl: './course-details.component.html',
  styleUrls: ['./course-details.component.scss']
})
export class CourseDetailsComponent implements OnInit {
  @Input()
  course?: Course;
  editCourse?: boolean;
  currentUser?: User;
  isStartDateInNextWeek?: boolean;
  isLecturer?: boolean;
  learningWays = Object.values(LearningWay);
  categories: Category[] = []

  constructor(private _categoryService:CategoryService) { }

  ngOnInit(): void {
    this.checkStartDate();
    const storedData = sessionStorage.getItem('userData');
    if (storedData) {
      this.currentUser = JSON.parse(storedData);
    }
    const storedIsLecturer = sessionStorage.getItem('isLecturer');
    if (storedIsLecturer) {
      this.isLecturer = JSON.parse(storedIsLecturer);
    }
    this._categoryService.getCategoriesFromServer().subscribe(data => {
      this.categories = data;
    })

  }
  checkStartDate(): void {

    if (this.course?.startDate) {
      const start = new Date(this.course.startDate);
      const today = new Date();
      const nextWeek = new Date();
      nextWeek.setDate(today.getDate() + 7); // Get date for the start of the next week
      this.isStartDateInNextWeek = start <= nextWeek && start >= today;
    }
  }
  ifEditCourse() {
    this.editCourse = true;
  }
  IconRouting(id: number) {
    return this.categories.filter(x => x.id == id)[0];
  }

}
<div class="card" style="width:400px" [ngClass]="{'active':isStartDateInNextWeek}">
    <img class="card-img-top"class="maxi" src={{course?.image}} alt="Card image">
    <div class="card-body">
       <h4 class="card-title">{{course?.name}}</h4>
       <strong>lesson's amount</strong>
       <div>{{course?.lessonsAmount}}</div>
        <ul class="card-text list-group">
            <strong>The syllabus is :</strong>
            <li *ngFor="let c of course?.courseSyllabus" class="list-group-item">
                <img class="mini"src="../../../../assets/checked.png"/>
                {{c}}
            </li>
        </ul>
        <div>
            <img src={{IconRouting(course?.categoryId!).iconRouting}} class="mini" alt=""/>
            {{IconRouting(course?.categoryId!).name}}
        </div>
        <p><strong>Learning Way:</strong> {{learningWays[course?.learningWay!]}}<img style="height: 50px ; width: 50px;" class="mini" src="{{course?.learningWay|IconPipe}}"></p>
        <div>{{ course?.startDate | date: 'shortDate' }}</div>
        <button  (click)="ifEditCourse()" () *ngIf="isLecturer&&currentUser!.id==course?.lecturerId!" class="btn btn-primary">Update Details</button>

        <edit-course [course]="course"*ngIf="editCourse&&isLecturer&&currentUser!.id==course?.lecturerId!"></edit-course>

    </div>
</div>
