import { Component, OnInit } from '@angular/core';
import { Student } from '../student.model';
import { Test } from '../test.model';
@Component({
  selector: 'students-list',
  templateUrl: './students-list.component.html',
  styleUrls: ['./students-list.component.scss']

})
export class StudentsListComponent implements OnInit {
  students: Student[] = [{ id: 1, name: "fghj", phon: "13456", family: "jk", addres: "hjk 5", avg: 89, works: false, dateLeave: new Date(), tests: [{ code: 1, date: new Date(), description: "jkk" }, { code: 2, date: new Date(), description: "yy" }] }, { id: 2, name: "fgk", phon: "1345678956", family: "hjkjk", addres: "hjk 9", avg: 100, works: true }];
  ngOnInit(): void {
  }
  selectedStudent?: Student;
  selectedTestList?: Test;
  constructor() { }
  deleteStudent(student: Student) {
    let indexOfStudent = this.students.indexOf(student);
    this.students.splice(indexOfStudent, 1);
  }
  selectS(student: Student) {
    this.selectedStudent = student;
    this.selectedTestList = student.tests;
  }
  student?: Student;
  AddStudent() {
    this.student = { id: 0, name: "", phon: "", family: "", addres: "", avg: 0, works: false, dateLeave: new Date() };
    this.selectS(this.student);
  }
  addNewStudentToList(student: Student) {
    this.students.push(student);
    this.selectedStudent = undefined;
  }
}
<ul  class="list-group">
    <li *ngFor="let student of students"[ngClass]="{'noActive':student.works==false ,'avg':student.avg>90}">
        <div class="row">
            <div class="col-1">{{student.id}}</div>
            <div class="col-1">{{student.addres}}</div>
            <div class="col-1"> {{student.phon}}</div>
            <div class="col-1">{{student.works}}</div>
            <div class="col-1">{{student.family}}</div>
            <div class="col-1"> {{student.name}}</div>
            <div class="col-2">
                <button (click)="deleteStudent(student)">Delete Student</button>
            </div>
            <div class="col-2">
                <button (click)="selectS(student)">Details Student</button>
            </div>
       </div> 
</li>
</ul>
<button (click)="AddStudent()">Add Student</button>
<test [tests]="selectedTestList"></test>
<single-student-details [student]="selectedStudent" (onSaveNewStudent)="addNewStudentToList($event)"(onDeleateStudent)="deleteStudent($event)"></single-student-details>
