import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { User } from '../users/user.model';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../users/user.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent implements OnInit {
  users: User[] = [];
  usersForm: FormGroup;
  userStorage?:User[];
  userStorag?:User;
  @Input()
  userName?: string;

  constructor(private _userService: UserService, private router: ActivatedRoute, private _router: Router) {
    this.usersForm = new FormGroup({
      name: new FormControl('', [Validators.required, Validators.minLength(2)]),
      password: new FormControl('', [Validators.required, Validators.minLength(4), Validators.maxLength(4)]),
      address: new FormControl(''),
      email: new FormControl('', [Validators.required, Validators.email])
    });
  }

  ngOnInit(): void {
    console.log(this.userName)
    this.router.queryParams.subscribe(params => {
      this.usersForm.patchValue({
        name: params['userName']
      });
    });
    this._userService.getUsersFromServer().subscribe(data => {
      this.users = data;
    });
  }
  addUser() {
    const userToAdd = {
      name: this.usersForm.value.name,
      password: this.usersForm.value.password,
      address: this.usersForm.value.address,
      email: this.usersForm.value.email
    };
    if (this.users.find(x => x.name == userToAdd.name && x.password == userToAdd.password && x.address == userToAdd.address && x.email == userToAdd.email)) {
      Swal.fire("this user already exists!!!");
      this._userService.getUsersFromServer().subscribe(s =>{
        this.userStorage=s;
        console.log("us"+ this.userStorage)
        this.userStorag=this.userStorage.find(x => x.name == userToAdd.name && x.password == userToAdd.password && x.address == userToAdd.address && x.email == userToAdd.email)
        sessionStorage.setItem('userData', JSON.stringify(userToAdd));
        console.log("j"+ this.userStorag)
       })
      this._router.navigate(['/allCourses']);
    }
    else {
      this._userService.addUser(userToAdd).subscribe(s => {
        console.log("s", s);
        if (s == true){
            this._userService.getUsersFromServer().subscribe(s =>{
     this.userStorage=s;
     console.log("us"+ this.userStorage)
     this.userStorag=this.userStorage.find(x => x.name == userToAdd.name && x.password == userToAdd.password && x.address == userToAdd.address && x.email == userToAdd.email)
     console.log("j"+ this.userStorag)
    })

          this._router.navigate(['/allCourses']);}
        else
          alert("false");
      })
    }
  }
}
<form [formGroup]="usersForm">
    <div class="form-group">
        <label for="usr">Name:</label>
        <input type="text" class="form-control" id="usr" [value]="userName" formControlName="name">
        <div class="alert alert-danger" *ngIf="usersForm.controls['name'].errors?.['required']">required!!</div>
        <div class="alert alert-danger" *ngIf="usersForm.controls['name'].errors?.['minlength']">the minimum
            is 2
        </div>
    </div>

    <div class="form-group">
        <label for="pwd">Password:</label>
        <input type="password" class="form-control" id="pwd" formControlName="password">
        <div class="alert alert-danger" *ngIf="usersForm.controls['password'].errors?.['required']">required!!</div>
        <div class="alert alert-danger" *ngIf="usersForm.controls['password'].errors?.['minlength']">the minimum
            is 4
        </div>
        <div class="alert alert-danger" *ngIf="usersForm.controls['password'].errors?.['maxlength']">the maximum
            is 4
        </div>
    </div>

    <div class="form-group">
        <label for="address">Address:</label>
        <input type="text" class="form-control" id="address" formControlName="address">
    </div>
    <div class="form-group">
        <label for="email">Email:</label>
        <input type="email" class="form-control" id="email" formControlName="email">
        <div class="alert alert-danger" *ngIf="usersForm.controls['email'].errors?.['required']">required!!</div>
        <div class="alert alert-danger" *ngIf="usersForm.controls['email'].errors?.['email']">your email
            is invalid
        </div>
    </div>

</form>
<button class="btn btn-success" (click)="addUser()" [disabled]="!usersForm.valid" >Register</button>
