import { Component, OnInit } from '@angular/core';
import { Student } from '../student.model';
import { Test } from '../test.model';
import { studentService } from '../studentService.service';
import { Router ,ActivatedRoute} from '@angular/router';
@Component({
  selector: 'students-list',
  templateUrl:'./students-list.component.html',
  styleUrls: ['./students-list.component.scss']
})
export class StudentsListComponent implements OnInit{
  students:Student[]=[];
  userId:string|null|undefined|number;
  ngOnInit(): void {
    this._acr.paramMap.subscribe(paramMap=>{
      if(paramMap.has("user")){
        this.userId=+paramMap?paramMap.get("user"):null;
      }});
   this.sS?.getStudents().subscribe(students=>{
    if(this.userId!=null&&this.userId!=undefined){
      this.students=students.filter(x=>x.id==+this.userId)
    }
    this.students=students},err=>{console.log("err");})
  }
  sS?:studentService;
  selectedStudent?:Student;
  selectedTestList?:Test[]|undefined;
  selectedIdStudent?:number;
  constructor(private _studentService:studentService,private _router:Router,private _acr:ActivatedRoute){
  this.sS=_studentService;
}
deleteStudent(student:Student){
  let indexOfStudent=this.students.indexOf(student);
  this.students.splice(indexOfStudent,1);
  this.sS?.deleteStudent(student).subscribe(s=>console.log("succeed"),err=>console.log("err"));
  if(student==this.selectedStudent)
    this.selectedStudent=undefined;
  if(student.id==this.selectedIdStudent){
    this.selectedIdStudent=undefined;
    this.selectedTestList=undefined;
  }
}
showStudentByActive(active:boolean){
this.sS?.getStudentsActive(active).subscribe(s=>this.students=s,err=>console.log("err"))
}
selectS(student:Student){
this._router.navigate(["/students",student.id]);
this.selectedStudent=student;
}
details(student:Student){
  student.tests!=undefined?this.selectedTestList=student.tests:this.selectedTestList=undefined;
  this.selectedIdStudent=student.id;
}
student?:Student;
AddStudent(){
  this.student={id:0,name:"",phon:"",family:"",mail:"",addres:"",avg:0,active:true};
  this.selectS(this.student);
}
addNewStudentToList(student:Student){
  this.students.push(student);
  this.sS?.addStudents(student).subscribe(succeed=>alert("succeed"),err=>{console.log("err")});
  this.selectedStudent=undefined;
} 
}
<input type="checkbox"#chb (change)="showStudentByActive(chb.checked)"/>
<ul  class="list-group">
    <li *ngFor="let student of students"[ngClass]="{'noActive':student.active==false ,'avg':student.avg>90}">
        <div class="row">
            <div class="col-1">{{student.id}}</div>
            <div class="col-1">{{student.addres}}</div>
            <div class="col-1"> {{student.phon}}</div>
            <div class="col-1">{{student.family}}</div>
            <div class="col-1"> {{student.name}}</div>
            <div class="col-1">
                <button (click)="deleteStudent(student)">Delete Student</button>
            </div>
            <div class="col-1">
                <button (click)="selectS(student)">Edit Student</button>
            </div>
            <div class="col-1">
                <button (click)="details(student)">Student's tests</button>
            </div>
       </div> 
    </li>
</ul>
<button (click)="AddStudent()">Add Student</button>
<test [tests]="selectedTestList"[idStudent]="selectedIdStudent"></test>
<single-student-details [student]="selectedStudent" (onSaveNewStudent)="addNewStudentToList($event)"(onDeleateStudent)="deleteStudent($event)"></single-student-details>
