import { Component, EventEmitter, HostListener, Input, OnInit, Output } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Course, LearningWay } from '../course.model';
import { Category } from '../../categories/category.model';
import { CourseService } from '../course.service';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { CategoryService } from '../../categories/category.service';
@Component({
  selector: 'app-add-course',
  templateUrl: './add-course.component.html',
  styleUrls: ['./add-course.component.scss']
})
export class AddCourseComponent implements OnInit {
  @Input()
  course?: Course;

  @Input()
  isEdit?: boolean;

  @Output()
  courseChanged : EventEmitter<void>=new EventEmitter<void>();
  name?: string = this.course?.name;
  startDate?: Date = this.course?.startDate;
  image?: string = this.course?.image;
  learningWay?: number = this.course?.learningWay;
  categoryId?: number = this.course?.categoryId;
  inputs: string[] = [" "]; // מערך שמכיל את תיבות הקלט, מתחיל עם תיבה ריקה בהתחלה
  l: number = this.inputs.length;
  categories?: Category[];
  courseForm: FormGroup;
  constructor(private route: ActivatedRoute, private _courseService: CourseService, private _categoryService: CategoryService, private _router: Router) {
    this.courseForm = new FormGroup({
      name: new FormControl(this.name, [Validators.required, Validators.minLength(2)]),
      startDate: new FormControl('', [Validators.required]),
      image: new FormControl('', [Validators.required]),
      learningWay: new FormControl('', [Validators.required]),
      categoryId: new FormControl('', [Validators.required])
    });
    console.log(this.name)
    //   this.route.queryParams.subscribe((params: Course ) => {
    //     if (params) {
    //       this.course=params;}
    // });
  }
  ngOnInit(): void {
    console.log("course", this.name)
    this._categoryService.getCategoriesFromServer().subscribe(data => {
      this.categories = data;
    })
  }


  onInput(event: Event, index: number): void {

    const target = event.target as HTMLInputElement;
    const value = target.value.trim();

    if (value && index === this.inputs.length - 1) {
      this.inputs.push(value);
    }
    else if (!value && index < this.inputs.length - 1) {
      this.inputs.splice(index + 1, 1);
    }
    //   this.l=this.inputs.length;
    //else if(value){ this.inputs[index]=value}
    console.log(this.inputs)
  }
  saveNewCourse() {
    const courseToAdd = {
      name: this.courseForm.value.name,
      startDate: this.courseForm.value.startDate,
      image: this.courseForm.value.image,
      categoryId: this.courseForm.value.categoryId,
      lessonsAmount: this.inputs.length - 1,
      courseSyllabus: this.inputs.splice(1),
      lecturerId:+JSON.parse(sessionStorage.getItem('userData')!)?.id,
      learningWay: +this.courseForm.value.learningWay
    };
    if (!this.isEdit) {
      console.log(courseToAdd)
      this._courseService.addCourse(courseToAdd).subscribe(s => {
        console.log("s", s);
        if (s == true) {
          Swal.fire("Success", "The course was saved successfully!!!", "success");
          this._router.navigate(['/allCourses']);
        }
        else
          Swal.fire('Error', 'Cannot add', 'error');
      })
    }
    else {
      this._courseService.editCourse(this.course?.id!, courseToAdd).subscribe(s => {
        if (s == true) {
          Swal.fire("Success", "The course was updated successfully!!!", "success");
          this.courseChanged.emit();

          this._router.navigate(['/allCourses']);
        }
        else
          Swal.fire('Error', 'Cannot Update', 'error');

      })
    }

  }

}


<form [formGroup]="courseForm">
    <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" class="form-control" id="name" [value]="course?.name" formControlName="name">
        <div class="alert alert-danger" *ngIf="courseForm.controls['name'].errors?.['required']">required!!</div>
        <div class="alert alert-danger" *ngIf="courseForm.controls['name'].errors?.['minlength']">the minimum
            is 2
        </div>
    </div>

    <div class="form-group">
        <label for="startDate">Start Date:</label>
        <input type="date" class="form-control" id="startDate" [value]="course?.startDate" formControlName="startDate">
        <div class="alert alert-danger" *ngIf="courseForm.controls['startDate'].errors?.['required']">required!!</div>

    </div>
    <div class="form-group">
        <label for="image">Image:</label>
        <input type="text" class="form-control" id="image" [value]="course?.image" formControlName="image">
        <div class="alert alert-danger" *ngIf="courseForm.controls['image'].errors?.['required']">required!!</div>

    </div>
    <label for="syl">The subjects of the lesson:</label>
    <ng-container *ngFor="let input of inputs; let i = index">
        <input id="syl" [value]="course?.courseSyllabus" class="form-control" (change)="onInput($event, i)">
        <div class="alert alert-danger" *ngIf="inputs.length<2">required!!</div>
    </ng-container>
    <div class="form-group">
        <label for="learningWay">Learning Way:</label>
        <br>
        <input type="radio" value=0 name="learningWay" formControlName="learningWay" />Frontal
        <br>
        <input type="radio" value=1 name="learningWay" formControlName="learningWay" />Zoom
    </div>
    <div class="form-group">
        <label for="categoryId">Category:</label>
        <select class="form-control" [value]="categoryId" formControlName="categoryId">
            <option *ngFor="let category of categories" [value]="category.id">{{category.name}}</option>
        </select>
        <div class="alert alert-danger" *ngIf="courseForm.controls['categoryId'].errors?.['required']">required!!</div>
    </div>

    <button class="btn btn-primary" (click)="saveNewCourse()" [disabled]="!courseForm.valid||inputs.length<2"
        >Save</button>
</form>
